class PostComments{constructor(e){this.postId=e,this.postContainer=$("#post-"+e),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))})),$(" .comment-like",this.postContainer).each((function(){t.likeComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$("#post-comments-"+e).prepend(o),t.deleteComment($(" .delete-comment-button",o)),t.likeComment($(" .comment-like",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<li id="comment-${e._id}">\n        <p>\n            <small><a class="delete-comment-button" href="/comments/delete/${e._id}">X</a></small>\n            ${e.content} <br>\n                <small>\n                ${e.user.name}\n                </small>\n                <div id="like_div_comment">\n                    <a href="/likes/toggle/?id=${e._id}&type=Comment" class="comment-like" id="like-${e._id}">\n                        <i class="far fa-thumbs-up like_icon_comment "></i>\n                        <i class="fas fa-thumbs-up like_icon_comment hide"></i>  \n                    </a>\n                </div>\n        </p>\n        <p id="count-${e._id}">likes: <strong id="counts">0</strong></p>\n    </li>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$("#comment-"+e.data.comment_id).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}likeComment(e){let t=$(e).prop("id"),n=$(`#${t} .like_icon_comment`),o=t.slice(5);$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){n.toggleClass("hide");var t=$(`#count-${o} #counts`).text();e.data.deleted?t--:t++,$(`#count-${o} strong`).html(t)},error:function(e){console.log(e)}})}))}}